mapper.select:
SELECT u.user_id, u.user_password, u.user_name FROM wave_user u
LEFT JOIN wave_user_cpny uc ON u.user_id = uc.user_id
WHERE u.user_id = #{userId}
AND uc.cpny_id = #{companyId}

SELECT a.action_id, a.action_name FROM wave_action a LEFT JOIN wave_role_module rm ON a.module_id = rm.module_id LEFT JOIN wave_user_role ur ON ur.role_id = rm.role_id
ur.user_id = #{userId}
AND a.module_id = #{moduleId}
ORDER BY a.index ASC
